type.read:
int i1 = read();
int i2 = read();
return (short) ((i1 << 8) + (i2));

int i1 = read();
int i2 = read();
return (i1 << 8) + (i2);

type.write:
buffer[0] = (byte) ((value >>> 56) & 0xFF));
buffer[1] = (byte) ((value >>> 48) & 0xFF));
buffer[2] = (byte) ((value >>> 40) & 0xFF));
buffer[3] = (byte) ((value >>> 32) & 0xFF));
buffer[4] = (byte) ((value >>> 24) & 0xFF));
buffer[5] = (byte) ((value >>> 16) & 0xFF));
buffer[6] = (byte) ((value >>>  8) & 0xFF));
buffer[7] = (byte) ((value       ) & 0xFF));
write(buffer, 8);

type.read:
int varint = 0;
for(int i = 0; i < 5; i++) {
	int b = read();
	if(i == 4 && (b & 0xF0) != 0)
		error("Varint too big");

	varint |= (b & 0x7F) << (i * 7);
	if((b & 0x80) == 0)
		break;
}
return varint;

long varlong = 0;
for(int i = 0; i < 10; i++) {
	int b = read();
	if(i == 9 && (b & 0xF0) != 0)
		error("Varlong too big");

	varlong |= ((long) b & 0x7F) << (i * 7);
	if((b & 0x80) == 0)
		break;
}
return varlong;

int limit = (Integer) option("limit");
boolean trim = (Boolean) option("trim");

int length = read("varint");
read(buffer, length);
String string = new String(buffer, 0, length, (java.util.Charset) variable("utf8"));

if(string.length() > limit) {
	if(trim)
		string = string.substring(0, limit);
	else
		error("String too big: " + string.length() + " > " + limit);
}

return string;

type.write:
	int limit = (Integer) option("limit");
	boolean trim = (Boolean) option("trim");

    if(value.length() > limit) {
    	if(trim)
    		value = value.substring(0, limit);
    	else
    		error("String too big: " + value.length() + " > " + limit);
    }

    byte[] bytes = value.getBytes((java.util.Charset) variable("utf8"));
    write("varint", bytes.length);
    write(bytes);
